import React, { Component } from "react";
import "./style.css";

let hearts = 3;

class Header extends Component {
    state = {
        hearts
    };

    renderHearts() {
        let divs = [];

        for (let i = 0; i < this.state.hearts; i++) {
            divs.push(<div key={i} className="heart"></div>);
        }

        return <div>{divs}</div>;
    };

    render() {

        return (
            <div class="container-fluid scoreboard">
                <div className="row scoring">
                    <div className="heartWrapper col-3">
                        <div className="heartsTitle row">Lives Left</div>
                        <div className="hearts row">{this.renderHearts()}</div>
                    </div>
                    <div className="gameTitle col-6">
                        <h1 className="gameTitleMain">Game of Thrones Memory Game</h1>
                        <h3 className="rules">Click as many Game of Thrones characters without clicking them twice!</h3>
                    </div>
                    <div className="col-3 scores">
                        <div className="currentScore score row"> Current Score: {this.props.currentScore} </div>
                        <div className="topScore score row">Top Score: {this.props.topScore}</div>
                    </div>
                </div>
                <div className="row message">
                <h3 className="messageText">{this.state.message}</h3>
                </div>
            </div >
        );
    }
}

export default Header;
