import React, { Component } from "react";
import Card from '../CharacterIcons/Card';
import './style.css';

class Gameboard extends Component {

    state = {
        Characters,
        topScore,
        guessesCorrect,
        message,
        hearts
    };

    setClicked = id => {
        const Characters = this.state.Characters;
        const cardClicked = Characters.filter(Character => Character.id === id);

        if (cardClicked[0].clicked) {

            guessesCorrect = 0;
            message = 'Whoops. Start over';

            // change to map or smth?
            for (let i = 0; i < Characters.length; i++) {
                Characters[i].clicked = false;
            }

            this.setState({ message });
            this.setState({ guessesCorrect });
            this.setState({ Characters });

        } else {
            cardClicked[0].clicked = true;

            guessesCorrect = guessesCorrect + 4;
            message = "Good Job!"

            if (guessesCorrect > topScore) {
                topScore = guessesCorrect;
                hearts++;
                this.setState({ hearts });
                this.setState({ topScore });
                this.renderHearts();
            }

            Characters.sort((a, b) => {
                return 0.5 - Math.random();
            });

            this.setState({ Characters });
            this.setState({ guessesCorrect });
            this.setState({ message });
        }
    };

    render() {

        return (
            <div className="row">
                {this.state.Characters.map(Character => (
                    <Card
                        setClicked={this.setClicked}
                        id={Character.id}
                        key={Character.id}
                        image={Character.image}
                        name={Character.name}
                        className="col-sm-1"
                    />
                ))}
            </div>
        );
    }
};

export default Gameboard;
