import React, { Component } from "react";

let topScore = 0;
let guessesCorrect = 0;
let hearts = 0;
let message = "";

// prop is a tyle of alert
class Header extends Component {
    state = {
        topScore,
        guessesCorrect,
        message,
        hearts
    };

    renderHearts() {
        let divs = [];

        for (let i = 0; i < this.state.hearts; i++) {
            divs.push(<div key={i} className="heart"></div>);
        }

        return <div>{divs}</div>;
    };

    render() {

        return (
            <div class="container-fluid">
                <div className="row">
                    <div className="heartWrapper col-4">
                        <div className="heartsTitle row">Lives Left</div>
                        {this.renderHearts()}
                    </div>
                    <div className="gameTitle col-4">
                        <h1 className="banner">Game of Thrones Memory Game</h1>
                        <h3 className="rules">Click as many GOT characters without clicking them twice!</h3>
                        <h3 className="message">{this.state.message}</h3>
                    </div>
                    <div className="col-4 scores">
                        <div className="currentScore score row"> Current Score: {this.props.currentScore} </div>
                        <div className="topScore score row">Top Score: {this.props.topScore}</div>
                    </div>
                </div>
            </div >
        );
    }
}

export default Header;
