import React, { Component } from "react";

let topScore = 0;
let guessesCorrect = 0;
let hearts = 0;
let message = "";

// prop is a tyle of alert
class Header extends Component {
    state = {
		topScore,
		guessesCorrect,
		message, 
		hearts
    };

    renderHearts() {
		let divs = [];

		for (let i = 0; i < this.state.hearts; i++) {
			divs.push(<div key={i} className="heart"></div>);
		}

		return <div>{divs}</div>;
	};
}

export default Header;
